parameters.inputSchema:
{
    "name": "chart_configuration",
    "description": "Configuration schema for Chart.js charts",
    "strict": true,
    "schema": {
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "enum": ["bar", "line", "radar", "pie", "doughnut", "polarArea", "bubble", "scatter", "area"]
    },
    "data": {
      "type": "object",
      "properties": {
        "labels": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "datasets": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "label": {
                "type": ["string", "null"]
              },
              "data": {
                "type": "array",
                "items": {
                  "type": "number"
                }
              },
              "backgroundColor": {
                "type": ["array", "null"],
                "items": {
                  "type": "string"
                }
              },
              "borderColor": {
                "type": ["array", "null"],
                "items": {
                  "type": "string"
                }
              },
              "borderWidth": {
                "type": ["number", "null"]
              }
            },
            "required": ["data", "label", "backgroundColor", "borderColor", "borderWidth"],
            "additionalProperties": false
          }
        }
      },
      "required": ["labels", "datasets"],
      "additionalProperties": false
    },
    "options": {
      "type": "object",
      "properties": {
        "scales": {
          "type": ["object", "null"],
          "properties": {
            "yAxes": {
              "type": "array",
              "items": {
                "type": ["object", "null"],
                "properties": {
                  "ticks": {
                    "type": ["object", "null"],
                    "properties": {
                      "max": {
                        "type": ["number", "null"]
                      },
                      "min": {
                        "type": ["number", "null"]
                      },
                      "stepSize": {
                        "type": ["number", "null"]
                      },
                      "beginAtZero": {
                        "type": ["boolean", "null"]
                      }
                    },
                    "required": ["max", "min", "stepSize", "beginAtZero"],
                    "additionalProperties": false
                  },
                  "stacked": {
                    "type": ["boolean", "null"]
                  }
                },
                "required": ["ticks", "stacked"],
                "additionalProperties": false
              }},
              "xAxes": {
                "type": ["object", "null"],
                "properties": {
                  "stacked": {
                    "type": ["boolean", "null"]
                  }
                },
                "required": ["stacked"],
                "additionalProperties": false
              }
          },
          "required": ["yAxes", "xAxes"],
          "additionalProperties": false
        },
        "plugins": {
          "type": ["object", "null"],
          "properties": {
            "title": {
              "type": ["object", "null"],
              "properties": {
                "display": {
                  "type": ["boolean", "null"]
                },
                "text": {
                  "type": ["string", "null"]
                }
              },
              "required": ["display", "text"],
              "additionalProperties": false
            },
            "legend": {
              "type": ["object", "null"],
              "properties": {
                "display": {
                  "type": ["boolean", "null"]
                },
                "position": {
                  "type": ["string", "null"],
                  "enum": ["top", "left", "bottom", "right", null]
                }
              },
              "required": ["display", "position"],
              "additionalProperties": false
            }
          },
          "required": ["title", "legend"],
          "additionalProperties": false
        }
      },
      "required": ["scales", "plugins"],
      "additionalProperties": false
    }
  },
  "required": ["type", "data", "options"],
  "additionalProperties": false
}
}

{
"type": "object",
"properties": {
	"query": {
		"type": "string",
		"description": "a query describing the chart to generate in json format"
		},
    "event": {
         "type": "string",
          "description": "the type of event.  this should always be set to Generate"
	}
}
}

{
"type": "object",
"properties": {
	"query": {
		"type": "string",
		"description": "the users query"
		},
    "event": {
         "type": "string",
          "description": "the type of event.  this should always be set to Data"
	}
}
}
