/**
 * INPUT expected in the item that arrives here:
 * {
 *   "sessionId": "13af3c0d203742098ea54adb0563f53a",
 *   "chatInput": "hi from curl/Postman"
 * }
 */
await $chat.sendMessage({
  sessionId: $('When chat message received').first().json.sessionId,   // same conversation
  role:      'user',            // or 'assistant'
  content:   $('When chat message received').first().json.chatInput    // text to display
});

// Pass data through unchanged (optional)
return $input.all();
