const documentContent = $input.item.json?.data || $input.item.json?.text;

if (!documentContent) {
    throw new Error('No document found in input');
}

function cleanText(text) {
    return text.replace(/\s+/g, ' ').trim();
}

// Split the document by product separator "---"
const productSeparator = "---";
const productStrings = documentContent.split(productSeparator).filter(item => item.trim());

// Process each product into its own chunk
const chunks = [];
let chunkNumber = 1;

productStrings.forEach(productString => {
    const cleanedProduct = cleanText(productString);

    if (cleanedProduct) {
        chunks.push({
            content: cleanedProduct,
            chunk: chunkNumber,
            chunk_size: cleanedProduct.length,
            product_sku: extractSKU(cleanedProduct) // Extract SKU for reference
        });
        chunkNumber++;
    }
});

// Helper function to extract SKU from product text
function extractSKU(productText) {
    const skuMatch = productText.match(/Product SKU:\s*([^\s]+)/);
    return skuMatch ? skuMatch[1] : 'unknown';
}

const returnData = chunks.map(chunk => ({
    json: chunk
}));

return returnData;
