for (const item of items) {
  let t = item.json.text;
  t = t.replace(/\\u[0-9A-Fa-f]{0,4}/g, '');
  t = t.replace(/\u0000/g, '');
  t = t.replace(/[\x00-\x1F\x7F]/g, '');
  t = t.normalize('NFKD');
  item.json.text = t;
}
return items;
